statements.select:
WITH T AS( SELECT Id,Category, case Category when 1 then '实用' when 2 then '有趣' when 3 then '热点' when 4 then '随笔' when 5 then '读书' end CategoryName, Title,PageView,Content,CONVERT(varchar, CreateDate, 120 ) CreateDate,Status,Description FROM Tab_Article
Status= #Status# Category= #Category#
) SELECT TOP $Rows$ * FROM T WHERE Id NOT IN ( SELECT TOP ($Rows$*($Page$-1)) Id FROM T ORDER BY CreateDate DESC ) ORDER BY CreateDate DESC

SELECT [Id], [ArticleId], [CreateTime], [CommentUserNickname], [CommentUserIp], [CommentContent], [Status] FROM Tab_ArticleComment
Status= #Status# ArticleId= #ArticleId#
ORDER BY CreateTime DESC;
